create database dw2;
use dw2;
create table Filme(id int AUTO_INCREMENT,title varchar(70),release_year int,language varchar(45),lenght int,rating double, PRIMARY KEY (id));
create table Receita(id int AUTO_INCREMENT,pais varchar(70),valor int,filmeId int, PRIMARY KEY (id), FOREIGN KEY (filmeId) REFERENCES Filme(id));
create table Ator(id int AUTO_INCREMENT, name varchar(70), artistic_name varchar(70), birth_date varchar(45), PRIMARY KEY (id) );
create table FilmeAtor(id int AUTO_INCREMENT, filmeId int NOT NULL, atorId int NOT NULL, PRIMARY KEY (id), Foreign Key (filmeId) References Filme(id), Foreign Key (atorId) References Ator(id));


